package com.loop;

public class EvenDoWhile 
{
   
	public static void evendo( )
    {
  	  int i=121;
  	  do
  	  {
  		  if(i%2==0)
  		      System.out.println(i);
  		  i++;
  	  }while(i<=229);
  	  
     
  	  
    }
	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
         evendo();
	}

}
