package com.abstract1;

public class Pattern 
{
    public static void patterns()
    {
    	for(int i=5;i>=1;i--)
    	{
    		for(int j=1;j<=i;j++)
    		{
    			System.out.print(j);
    		}
    		System.out.println("");
    	}
    	
    }
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		patterns();
	}

}
